import React, { useState } from 'react';
import './App.css';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithPopup, GoogleAuthProvider } from 'firebase/auth';
import googleLogo from './images/google-logo.png';
import AdminDashboard from './components/AdminDashboard';

// ✅ Your Firebase config — replace with yours if different
const firebaseConfig = {
  apiKey: "AIzaSyDPfSUclIb-OnOUoK71kO0J4eBln1IbIpM",
  authDomain: "the-better-india-comments.firebaseapp.com",
  projectId: "the-better-india-comments",
  storageBucket: "the-better-india-comments.appspot.com",
  messagingSenderId: "760843686957",
  appId: "1:760843686957:web:0da41b2365e2598c4c94a4",
  measurementId: "G-YKF41C73YT"
};

// ✅ Initialize Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const provider = new GoogleAuthProvider();

function App() {
  const [user, setUser] = useState(null);

  const signInWithGoogle = () => {
    signInWithPopup(auth, provider)
      .then((result) => {
        const user = result.user;
        console.log('User signed in:', user);
        setUser(user);
      })
      .catch((error) => {
        console.error('Error during sign-in:', error);
        alert('Sign-in failed');
      });
  };

  return (
    <div className="app-container">
      {user ? (
        <AdminDashboard />
      ) : (
        <>
          <h1>Welcome to The Better India Comments</h1>
          <button className="google-btn" onClick={signInWithGoogle}>
            <img src={googleLogo} alt="Google logo" className="google-icon" />
            Sign in with Google
          </button>
        </>
      )}
    </div>
  );
}

export default App;
