//     <div style={{ padding: "20px" }}>
//       <h2>Welcome, {user.displayName}</h2>
//       <p>You are logged in as a regular user.</p>
//     </div>
//   );
// }

// export default UserDashboard;



import React, { useEffect, useState } from 'react';
import './UserDashboard.css';
import axios from 'axios';

const UserDashboard = ({ user }) => {
  const [posts, setPosts] = useState([]);
  const [activeTab, setActiveTab] = useState('view-posts');

  useEffect(() => {
    // Fetch admin posts
    axios.get('/api/posts')
      .then(res => setPosts(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleLike = async (postId) => {
    await axios.post(`/api/posts/${postId}/like`, { userId: user._id });
    // Refresh posts
    const updatedPosts = await axios.get('/api/posts');
    setPosts(updatedPosts.data);
  };

  const handleComment = async (postId, comment) => {
    await axios.post(`/api/posts/${postId}/comment`, {
      userId: user._id,
      comment,
    });
    const updatedPosts = await axios.get('/api/posts');
    setPosts(updatedPosts.data);
  };

  return (
    <div className="user-dashboard">
      <div className="sidebar">
        <h2>User Panel</h2>
        <ul>
          <li onClick={() => setActiveTab('view-posts')}>View Posts</li>
          <li onClick={() => setActiveTab('liked-posts')}>Liked Posts</li>
          <li onClick={() => setActiveTab('commented-posts')}>Commented Posts</li>
          <li onClick={() => setActiveTab('profile')}>Profile</li>
          <li onClick={() => {/* logout logic */}}>Logout</li>
        </ul>
      </div>

      <div className="content">
        {activeTab === 'view-posts' && (
          <>
            <h3>All Posts</h3>
            {posts.map(post => (
              <div key={post._id} className="post-card">
                <h4>{post.title}</h4>
                <p>{post.content}</p>
                {post.image && <img src={post.image} alt="post" style={{ width: '100%' }} />}
                
                <div className="button-group">
                  <button className="btn like-btn" onClick={() => handleLike(post._id)}>
                    ❤️ Like ({post.likes.length})
                  </button>
                  <CommentBox postId={post._id} onComment={handleComment} />
                </div>

                <div className="comments-section">
                  <h5>Comments:</h5>
                  <ul>
                    {post.comments.map((c, i) => (
                      <li key={i}><strong>{c.userName}</strong>: {c.text}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
          </>
        )}

        {activeTab === 'profile' && (
          <div>
            <h3>Profile</h3>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
          </div>
        )}

        {/* You can add liked-posts and commented-posts views similarly */}
      </div>
    </div>
  );
};

const CommentBox = ({ postId, onComment }) => {
  const [comment, setComment] = useState('');

  const handleSubmit = () => {
    if (comment.trim()) {
      onComment(postId, comment);
      setComment('');
    }
  };

  return (
    <div className="comment-box">
      <input
        type="text"
        value={comment}
        placeholder="Add a comment..."
        onChange={(e) => setComment(e.target.value)}
      />
      <button className="btn comment-btn" onClick={handleSubmit}>Comment</button>
    </div>
  );
};

export default UserDashboard;
