import React, { useState } from 'react';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [showUploadForm, setShowUploadForm] = useState(false);

  const handleUploadClick = () => {
    setShowUploadForm(!showUploadForm);
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <div className="dashboard-options">
        <button className="option-btn">View My Posts</button>
        <button className="option-btn">View Comments</button>
        <button className="option-btn" onClick={handleUploadClick}>
          {showUploadForm ? 'Hide Upload Form' : 'Upload a Post'}
        </button>
      </div>

      {showUploadForm && (
        <div className="upload-form">
          <h3>Upload New Post</h3>
          <form>
            <label>Title:</label>
            <input type="text" name="title" required />

            <label>Content:</label>
            <textarea name="content" rows="5" required></textarea>

            <label>Upload Image:</label>
            <input type="file" name="image" accept="image/*" />

            <button type="submit">Submit Post</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
