import React from 'react';
import './AdminDashboard.css'; // Reuse the CSS styles you already wrote

const mockPosts = [
  {
    title: 'Inspiring Story of a Farmer',
    description: 'This is the journey of a farmer who transformed a barren land into a green haven.',
    imageUrl: 'https://via.placeholder.com/600x300', // Placeholder image
  },
  {
    title: 'Clean Water Initiative',
    description: 'A community-driven project that brought clean water to a remote village.',
    imageUrl: 'https://via.placeholder.com/600x300',
  },
];

const ViewPosts = () => {
  return (
    <div className="content">
      <h2>Uploaded Posts</h2>
      {mockPosts.map((post, idx) => (
        <div key={idx} className="post-card">
          <h3>{post.title}</h3>
          <p>{post.description}</p>
          {post.imageUrl && <img src={post.imageUrl} alt={post.title} />}
          <div className="button-group">
            <button className="button edit-btn">Edit</button>
            <button className="button delete-btn">Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ViewPosts;
