import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  BookOpen,
  ThumbsUp,
  MessageCircle,
  User,
  LogOut,
  Menu
} from "lucide-react";
import { getAuth, signOut } from "firebase/auth";
import "./UserDashboard.css";

function UserDashboard({ user }) {
  const [activeTab, setActiveTab] = useState("view-posts");
  const [posts, setPosts] = useState([]);
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const isMobile = window.innerWidth <= 768;

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const res = await axios.get("/api/posts");
      setPosts(res.data);
    } catch (error) {
      console.error("Error fetching posts", error);
    }
  };

  const handleLike = async (postId) => {
    try {
      await axios.post(`/api/posts/${postId}/like`, { userId: user.uid });
      fetchPosts();
    } catch (error) {
      console.error("Like failed", error);
    }
  };

  const handleComment = async (postId, comment) => {
    if (!comment) return;
    try {
      await axios.post(`/api/posts/${postId}/comment`, {
        userId: user.uid,
        userName: user.displayName,
        text: comment
      });
      fetchPosts();
    } catch (error) {
      console.error("Comment failed", error);
    }
  };

  const handleLogout = async () => {
    const auth = getAuth();
    try {
      await signOut(auth);
      window.location.reload(); // or redirect to login page
    } catch (error) {
      console.error("Logout failed", error);
    }
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    if (isMobile) setSidebarOpen(false);
  };

  return (
    <div className={`user-dashboard ${sidebarOpen ? "sidebar-open" : ""}`}>
      <button className="hamburger" onClick={() => setSidebarOpen(!sidebarOpen)}>
        <Menu size={24} />
      </button>

      <aside className={`user-sidebar ${sidebarOpen ? "open" : ""}`}>
        <h2 className="user-greeting">Hello, {user.displayName}</h2>
        <ul className="user-menu">
          <li onClick={() => handleTabClick("profile")}>
            <User size={18} /> <span>Profile</span>
          </li>
          <li onClick={() => handleTabClick("view-posts")}>
            <BookOpen size={18} /> <span>View Posts</span>
          </li>
          <li onClick={() => handleTabClick("liked-posts")}>
            <ThumbsUp size={18} /> <span>Liked Posts</span>
          </li>
          <li onClick={() => handleTabClick("commented-posts")}>
            <MessageCircle size={18} /> <span>Commented Posts</span>
          </li>
          <li onClick={handleLogout}>
            <LogOut size={18} /> <span>Logout</span>
          </li>
        </ul>
      </aside>

      <main className="user-content">
        {activeTab === "view-posts" && (
          <>
            <h3>Admin Posts</h3>
            {posts.map((post) => (
  <div key={post._id} className="post-card">
    <h4>{post.title}</h4>
    <p>{post.content}</p>
    {post.imageUrl && (
      <img
        src={`http://localhost:5000${post.imageUrl}`}
        alt="post"
        className="post-image"
      />
    )}
    <div className="button-group">
      <button onClick={() => handleLike(post._id)}>
        ❤️ Like ({post.likes.length})
      </button>
    </div>
    <CommentBox postId={post._id} onSubmit={handleComment} />
    <div className="comments">
      {post.comments.map((c, i) => (
        <p key={i}>
          <strong>{c.userName}</strong>: {c.text}
        </p>
      ))}
    </div>
  </div>
))}

          </>
        )}

        {activeTab === "liked-posts" && <p>Show liked posts here</p>}
        {activeTab === "commented-posts" && <p>Show commented posts here</p>}
        {activeTab === "profile" && (
          <div>
            <h3>Profile Info</h3>
            <p><strong>Name:</strong> {user.displayName}</p>
            <p><strong>Email:</strong> {user.email}</p>
          </div>
        )}
      </main>
    </div>
  );
}

function CommentBox({ postId, onSubmit }) {
  const [comment, setComment] = useState("");

  return (
    <div className="comment-box">
      <input
        type="text"
        placeholder="Add a comment"
        value={comment}
        onChange={(e) => setComment(e.target.value)}
      />
      <button
        onClick={() => {
          onSubmit(postId, comment);
          setComment("");
        }}
      >
        Comment
      </button>
    </div>
  );
}

export default UserDashboard;
