import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pencil, Trash2 } from 'lucide-react';
import './ViewPosts.css';

const ViewPosts = () => {
  const [posts, setPosts] = useState([]);
  const [editPostId, setEditPostId] = useState(null);
  const [editedTitle, setEditedTitle] = useState('');
  const [editedDescription, setEditedDescription] = useState('');
  const [isSidebarOpen, setSidebarOpen] = useState(false);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const res = await axios.get('/api/posts');
      setPosts(res.data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  const deletePost = async (postId) => {
    try {
      await axios.delete(`/api/posts/${postId}`);
      fetchPosts();
    } catch (error) {
      console.error('Error deleting post:', error);
    }
  };

  const saveEdit = async (postId) => {
    try {
      await axios.put(`/api/posts/${postId}`, {
        title: editedTitle,
        description: editedDescription,
      });
      setEditPostId(null);
      fetchPosts();
    } catch (error) {
      console.error('Error updating post:', error);
    }
  };

  const startEdit = (post) => {
    setEditPostId(post._id);
    setEditedTitle(post.title);
    setEditedDescription(post.description);
  };

  return (
    <div className="dashboard-container">
      <button className="menu-toggle-btn" onClick={() => setSidebarOpen(!isSidebarOpen)}>
        ☰
      </button>

      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <h2>Admin Panel</h2>
        <hr />
        <ul>
          <li><span>📤</span> Upload Post</li>
          <li><span>📄</span> View My Posts</li>
          <li><span>💬</span> View Comments</li>
        </ul>
      </div>

      <div className="main-content">
        <h2 className="page-title">My Uploaded Posts</h2>
        <div className="view-posts">
          {posts.map((post) => (
            <div key={post._id} className="post-card">
              {editPostId === post._id ? (
                <>
                  <input
                    className="input-field"
                    value={editedTitle}
                    onChange={(e) => setEditedTitle(e.target.value)}
                  />
                  <textarea
                    className="input-field"
                    value={editedDescription}
                    onChange={(e) => setEditedDescription(e.target.value)}
                  />
                  <div className="button-group">
                    <button className="btn save-btn" onClick={() => saveEdit(post._id)}>Save</button>
                    <button className="btn cancel-btn" onClick={() => setEditPostId(null)}>Cancel</button>
                  </div>
                </>
              ) : (
                <>
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  {post.imageUrl && <img className="post-image" src={post.imageUrl} alt="Post" />}
                  <div className="button-group">
                    <button className="btn edit-btn" onClick={() => startEdit(post)}><Pencil size={16} /> Edit</button>
                    <button className="btn delete-btn" onClick={() => deletePost(post._id)}><Trash2 size={16} /> Delete</button>
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ViewPosts;
