import React, { useEffect, useState } from "react";
import axios from "axios";

const ViewComments = () => {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    // Fetch all comments across posts
    axios.get("http://localhost:5000/api/posts/comments")
      .then(res => setComments(res.data))
      .catch(err => console.error("Error fetching comments:", err));
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold mb-4">All Comments</h2>
      {comments.length === 0 ? (
        <p className="text-gray-600">No comments yet.</p>
      ) : (
        <div className="grid gap-4">
          {comments.map((comment, index) => (
            <div key={index} className="bg-white shadow-md rounded-xl p-4">
              <p className="text-sm text-gray-700"><strong>Post:</strong> {comment.postTitle}</p>
              <p className="text-sm text-gray-700"><strong>User:</strong> {comment.userName} ({comment.userId})</p>
              <p className="text-md mt-2 text-gray-800"><strong>Comment:</strong> {comment.text}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ViewComments;
