import React, { useState, useEffect } from 'react';
import './App.css';
import { signInWithPopup, onAuthStateChanged, signOut } from 'firebase/auth';
import { auth, provider } from './firebase';
import googleLogo from './images/google-logo.png';
import AdminDashboard from './components/AdminDashboard';
import UserDashboard from './components/UserDashboard';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import UpdateProfile from './pages/UpdateProfile';


function App() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });

    return () => unsubscribe(); // cleanup
  }, []);

  // âœ… Check profile only after user is authenticated
  useEffect(() => {
    const checkUserProfile = async () => {
      try {
        if (user?.email && user.email !== "hari1018vinod@gmail.com") {
          const res = await axios.get(`http://localhost:5000/api/users/byEmail/${user.email}`);
          const userData = res.data;

          if (!userData.profileCompleted) {
            navigate("/update-profile");
          }
        }
      } catch (err) {
        console.error("Error checking profile:", err);
      }
    };

    if (user) {
      checkUserProfile();
    }
  }, [user, navigate]);

  const signInWithGoogle = () => {
    signInWithPopup(auth, provider)
      .then((result) => {
        console.log('User signed in:', result.user);
      })
      .catch((error) => {
        console.error('Error during sign-in:', error);
        alert('Sign-in failed');
      });
  };

  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        console.log("User signed out");
      })
      .catch((error) => {
        console.error("Logout error:", error);
      });
  };

  if (user) {
    return user.email === "hari1018vinod@gmail.com"
      ? <AdminDashboard user={user} handleLogout={handleLogout} />
      : <UserDashboard user={user} handleLogout={handleLogout} />;
  }

  return (
    <div className="app-container">
      <h1>Welcome to The Better India Comments</h1>
      <button className="google-btn" onClick={signInWithGoogle}>
        <img src={googleLogo} alt="Google logo" className="google-icon" />
        Sign in with Google
      </button>
    </div>
  );
}

export default App;
