import React, { useState } from "react";
import "./AdminDashboard.css";
import axios from "axios";
import { FilePlus, MessageSquareText, LayoutList } from "lucide-react";

function AdminDashboard() {
  const [selectedOption, setSelectedOption] = useState("upload");

  // State to store post details
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  const handleUpload = async () => {
    try {
      const newPost = { title, content, imageUrl };
      await axios.post("http://localhost:5000/api/posts", newPost);
      alert("✅ Post uploaded successfully!");

      // Clear form
      setTitle("");
      setContent("");
      setImageUrl("");
    } catch (err) {
      console.error("Upload failed:", err);
      alert("❌ Failed to upload post");
    }
  };

  const renderContent = () => {
    switch (selectedOption) {
      case "upload":
        return (
          <div className="upload-form">
            <h2>Upload a New Post</h2>
            <input
              type="text"
              placeholder="Post Title"
              className="input-field"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <br />
            <textarea
              placeholder="Post Content"
              className="input-field"
              value={content}
              onChange={(e) => setContent(e.target.value)}
            ></textarea>
            <br />
            <input
              type="text"
              placeholder="Image URL"
              className="input-field"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
            />
            <br />
            <button className="submit-btn" onClick={handleUpload}>
              Submit Post
            </button>
          </div>
        );
      case "view":
        return <h2>Your Uploaded Posts will be listed here</h2>;
      case "comments":
        return <h2>View Comments on Your Posts</h2>;
      default:
        return null;
    }
  };

  return (
    <div className="admin-dashboard">
      <aside className="sidebar">
        <h1>Admin Panel</h1>
        <ul>
          <li onClick={() => setSelectedOption("upload")}>
            <FilePlus className="icon" /> Upload Post
          </li>
          <li onClick={() => setSelectedOption("view")}>
            <LayoutList className="icon" /> View My Posts
          </li>
          <li onClick={() => setSelectedOption("comments")}>
            <MessageSquareText className="icon" /> View Comments
          </li>
        </ul>
      </aside>
      <main className="content">{renderContent()}</main>
    </div>
  );
}

export default AdminDashboard;
