import React, { useState } from 'react';
import UploadPost from './UploadPost';
import './AdminDashboard.css';

function AdminDashboard({ user }) {
  const [activeTab, setActiveTab] = useState('');

  const renderContent = () => {
    switch (activeTab) {
      case 'upload':
        return <UploadPost />;
      case 'posts':
        return <div className="content-box">Your uploaded posts will appear here.</div>;
      case 'comments':
        return <div className="content-box">User comments will be shown here.</div>;
      default:
        return <div className="content-box">Select an option from the menu.</div>;
    }
  };

  return (
    <div className="admin-dashboard">
      <aside className="sidebar">
        <h2>Admin Panel</h2>
        <ul>
          <li onClick={() => setActiveTab('upload')}>Upload a Post</li>
          <li onClick={() => setActiveTab('posts')}>View My Posts</li>
          <li onClick={() => setActiveTab('comments')}>View Comments</li>
        </ul>
      </aside>
      <main className="main-content">
        <h2>Welcome, {user.displayName}</h2>
        {renderContent()}
      </main>
    </div>
  );
}

export default AdminDashboard;
