import React, { useState, useEffect } from "react";
import "./AdminDashboard.css";
import ViewPosts from './ViewPosts';

import axios from "axios";
import {
  FilePlus,
  MessageSquareText,
  LayoutList,
  Trash,
  Pencil,
  X,
  Save,
} from "lucide-react";

function AdminDashboard() {
  const [selectedOption, setSelectedOption] = useState("upload");
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [posts, setPosts] = useState([]);
  const [editingPostId, setEditingPostId] = useState(null);
  const [editedTitle, setEditedTitle] = useState("");
  const [editedContent, setEditedContent] = useState("");

  const fetchPosts = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/posts");
      setPosts(res.data);
    } catch (err) {
      console.error("Failed to fetch posts:", err);
    }
  };

  useEffect(() => {
    if (selectedOption === "view") {
      fetchPosts();
    }
  }, [selectedOption]);

  const handleUpload = async () => {
    if (!title || !content || !imageFile) {
      alert("All fields are required.");
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("title", title);
      formData.append("content", content);
      formData.append("image", imageFile);

      await axios.post("http://localhost:5000/api/posts", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("✅ Post uploaded successfully!");
      setTitle("");
      setContent("");
      setImageFile(null);
      fetchPosts();
    } catch (err) {
      console.error("Upload failed:", err);
      alert("❌ Failed to upload post");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (postId) => {
    if (!window.confirm("Are you sure you want to delete this post?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/posts/${postId}`);
      setPosts(posts.filter((post) => post._id !== postId));
    } catch (err) {
      console.error("Failed to delete post:", err);
      alert("❌ Failed to delete post");
    }
  };

  const startEditing = (post) => {
    setEditingPostId(post._id);
    setEditedTitle(post.title);
    setEditedContent(post.content);
  };

  const cancelEditing = () => {
    setEditingPostId(null);
    setEditedTitle("");
    setEditedContent("");
  };

  const saveEdit = async (postId) => {
    try {
      await axios.put(`http://localhost:5000/api/posts/${postId}`, {
        title: editedTitle,
        content: editedContent,
      });
      setEditingPostId(null);
      fetchPosts();
    } catch (err) {
      console.error("Failed to update post:", err);
      alert("❌ Failed to update post");
    }
  };

  const renderContent = () => {
    switch (selectedOption) {
      case "upload":
        return (
          <div className="upload-form">
            <h2>Upload a New Post</h2>
            <input
              type="text"
              placeholder="Post Title"
              className="input-field small-width"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <br />
            <textarea
              placeholder="Post Content"
              className="input-field small-width"
              value={content}
              onChange={(e) => setContent(e.target.value)}
            ></textarea>
            <br />
            <input
              type="file"
              accept="image/*"
              className="input-field small-width"
              onChange={(e) => setImageFile(e.target.files[0])}
            />
            <br />
            {imageFile && (
              <div>
                <p>Selected Image Preview:</p>
                <img
                  src={URL.createObjectURL(imageFile)}
                  alt="preview"
                  style={{ maxWidth: "200px", marginTop: "10px" }}
                />
              </div>
            )}
            <br />
            <button
              className="submit-btn"
              onClick={handleUpload}
              disabled={loading}
            >
              {loading ? "Uploading..." : "Submit Post"}
            </button>
          </div>
        );

      

      case "view":
  return <ViewPosts />;



      case "comments":
        return <h2>View Comments on Your Posts</h2>;

      default:
        return null;
    }
  };

  return (
    <div className="admin-dashboard">
      <aside className="sidebar">
        <h1>Admin Panel</h1>
        <ul>
          <li onClick={() => setSelectedOption("upload")}>
            <FilePlus className="icon" /> Upload Post
          </li>
          <li onClick={() => setSelectedOption("view")}>
            <LayoutList className="icon" /> View My Posts
          </li>
          <li onClick={() => setSelectedOption("comments")}>
            <MessageSquareText className="icon" /> View Comments
          </li>
        </ul>
      </aside>
      <main className="content">{renderContent()}</main>
    </div>
  );
}

export default AdminDashboard;
