import React, { useEffect, useState } from "react";
import axios from "axios";

const ViewComments = () => {
  const [comments, setComments] = useState([]);

  // Fetch all comments on load
  useEffect(() => {
    fetchComments();
  }, []);

  const fetchComments = () => {
    axios
      .get("http://localhost:5000/api/posts/comments")
      .then(res => setComments(res.data))
      .catch(err => console.error("Error fetching comments:", err));
  };

  // Delete comment by ID
  const handleDelete = (commentId) => {
    if (window.confirm("Are you sure you want to delete this comment?")) {
      axios
        .delete(`http://localhost:5000/api/posts/comments/${commentId}`)
        .then(() => {
          setComments(prev => prev.filter(c => c._id !== commentId));
        })
        .catch(err => console.error("Error deleting comment:", err));
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold mb-4">All Comments</h2>
      {comments.length === 0 ? (
        <p className="text-gray-600">No comments yet.</p>
      ) : (
        <div className="grid gap-4">
          {comments.map((comment) => (
            <div
              key={comment._id}
              className="bg-white shadow-md rounded-xl p-4 relative"
            >
              <p className="text-sm text-gray-700">
                <strong>Post:</strong> {comment.postTitle}
              </p>
              <p className="text-sm text-gray-700">
                <strong>User:</strong> {comment.userName}
              </p>
              <p className="text-md mt-2 text-gray-800">
                <strong>Comment:</strong> {comment.text}
              </p>
              <button
                onClick={() => handleDelete(comment._id)}
                className="absolute top-2 right-2 bg-red-500 text-white text-xs px-3 py-1 rounded hover:bg-red-600 transition"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ViewComments;
