import React, { useState, useEffect } from 'react';
import './App.css';
import { signInWithPopup, onAuthStateChanged, signOut } from 'firebase/auth';
import { auth, provider } from './firebase';
import googleLogo from './images/google-logo.png';
import AdminDashboard from './components/AdminDashboard';
import UserDashboard from './components/UserDashboard';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import UpdateProfile from './pages/UpdateProfile';

function App() {
  const [user, setUser] = useState(null);
  const [isAdminVerified, setIsAdminVerified] = useState(false);
  const [adminCode, setAdminCode] = useState('');
  const [profileCompleted, setProfileCompleted] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    const checkUserProfile = async () => {
      if (user?.email && user.email !== "hari1018vinod@gmail.com") {
        try {
          const res = await axios.get(`http://localhost:5000/api/users/byEmail/${user.email}`);
          const userData = res.data;

          if (!userData.profileCompleted) {
            setProfileCompleted(false);
            navigate("/update-profile");
          } else {
            setProfileCompleted(true);
            navigate("/user-dashboard");
          }
        } catch (err) {
          console.error("Error checking user profile:", err);
        }
      }
    };

    if (user && user.email !== "hari1018vinod@gmail.com") {
      checkUserProfile();
    }
  }, [user, navigate]);

  const signInWithGoogle = () => {
    signInWithPopup(auth, provider)
      .then((result) => {
        console.log('User signed in:', result.user);
      })
      .catch((error) => {
        console.error('Error during sign-in:', error);
        alert('Sign-in failed');
      });
  };

  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        console.log("User signed out");
        setUser(null);
        setIsAdminVerified(false);
        setAdminCode('');
      })
      .catch((error) => {
        console.error("Logout error:", error);
      });
  };

  const handleAdminCodeSubmit = () => {
    if (adminCode === '0618') {
      setIsAdminVerified(true);
    } else {
      alert('Invalid admin code');
    }
  };

  // If user is logged in
  if (user) {
    // Admin logic
    if (user.email === "hari1018vinod@gmail.com") {
      return isAdminVerified ? (
        <AdminDashboard user={user} handleLogout={handleLogout} />
      ) : (
        <div className="admin-code-container">
          <h2>Enter Admin Access Code</h2>
          <input
            type="password"
            placeholder="Enter 4-digit code"
            value={adminCode}
            onChange={(e) => setAdminCode(e.target.value)}
            className="p-2 border mb-4"
          />
          <button onClick={handleAdminCodeSubmit} className="bg-blue-500 text-white px-4 py-2 rounded">Verify</button>
        </div>
      );
    }

    // Non-admin user
    return profileCompleted ? (
      <UserDashboard user={user} handleLogout={handleLogout} />
    ) : null; // Already redirected to update-profile
  }

  // Not logged in
  return (
    <div className="app-container">
      <h1>Welcome to The Better India Comments</h1>
      <button className="google-btn" onClick={signInWithGoogle}>
        <img src={googleLogo} alt="Google logo" className="google-icon" />
        Sign in with Google
      </button>
    </div>
  );
}

export default App;
